import React from "react";
import './styles.css'

function DiceGame(){


    const diceImages = [
        require('./images/dice1.png'),
        require('./images/dice2.png'),
        require('./images/dice3.png'),
        require('./images/dice4.png'),
        require('./images/dice5.png'),
        require('./images/dice6.png')
      ];

    var rnd1=diceImages[Math.floor(Math.random()*6)+1];
    var rnd2=diceImages[Math.floor(Math.random()*6)+1];

    let source1= "./images/dice"+rnd1+".png"
    let source2= "./images/dice"+rnd2+".png"

    return(
        <div class='game'>
        <body>
            <div class="container">
            <h1>Refresh Me!</h1>
            <div class="dice">
                <p>Player 1</p>
                <img class="img" alt="uno" src={require(source1)} ></img>
            </div>

            <div class="dice">
                <p>Player 2</p>
                <img class="img" alt="due" src={require(source2)}></img>
            </div>
            <h1></h1>
            </div>    
        </body>    
        </div>
    );
}
export default DiceGame;
