import './ESAME1.css'
import React from 'react';

function ESAME1(){

    function aggiungiClasseMedio(){
        const larghezzaSchermo = window.innerWidth;
        const elemento = document.querySelector('#mid');
        if (larghezzaSchermo < 300 ){
            elemento.classList.add('deleteElem');
        }else{
            elemento.classList.remove('deleteElem');
        }
    }

    function aggiungiClassePiccolo(){
        const larghezzaSchermo = window.innerWidth;
        const elemento = document.querySelector('#small');
        if (larghezzaSchermo < 6000 ){
            elemento.classList.add('deleteElem');
        }else{
            elemento.classList.remove('deleteElem');
        }
    }

    window.addEventListener('load', aggiungiClassePiccolo);
    window.addEventListener('resize', aggiungiClassePiccolo);

    window.addEventListener('load', aggiungiClasseMedio);
    window.addEventListener('resize', aggiungiClasseMedio);


    return (
        <div>
            <div className='elem_in_alto'>

            </div>
            <div className='box'>
                <div>
                    uno
                </div>
                <div id='mid'>
                    due
                </div>
                <div id='small'>
                    tre
                </div>
            </div>
        </div>
    );
}
export default ESAME1
