import './styles.css'

function DiceGame(){
    var rnd1= Math.floor(Math.random()*6)+1;
    var rnd2= Math.floor(Math.random()*6)+1;

    let source1= "/images/dice"+rnd1+".png";
    let source2= "/images/dice"+rnd2+".png";

    var image1 = document.querySelectorAll("img")[0];
    document.querySelectorAll("img")[1].setAttribute("src", source2);

    if (rnd1 > rnd2) {
        document.querySelector("h1").innerHTML = "ðŸš© Play 1 Wins!";
      }
      else if (rnd2 > rnd1) {
        document.querySelector("h1").innerHTML = "Player 2 Wins! ðŸš©";
      }
      else {
        document.querySelector("h1").innerHTML = "Draw!";
      }

    return(
        <div class='game'>
        <body>
            <div class="container">
            <h1>Refresh Me!</h1>
            <div class="dice">
                <p>Player 1</p>
                <img class="img1" src={source1}></img>
            </div>

            <div class="dice">
                <p>Player 2</p>
                <img class="img2" src={source2}></img>
            </div>

            </div>    
        </body>    
        </div>
    );
}
export default DiceGame;
