import React, { useEffect, useState } from 'react';
import './Wapp.css';

function Wapp() {
    const apiKey = "39cca2a3cb09544ba65e52668421a919"; // Replace with your own API key
    const [weatherData, setWeatherData] = useState(null);
    const [error, setError] = useState(null);
    fetch('https://esempio.com/api/dati')
    .then(response => response.json()) //la risposta converte il testo in arrivo in json
    .then(data => console.log(data)) //il json viene stampato
    .catch(error => console.error(error));
    function clearWeatherCards() {
        setWeatherData(null); // Clear the weather data state
        setError(null); // Clear any previous errors
    }

    async function getWeather(selectedCity, apiKey) {
        clearWeatherCards();
        const url = `https://api.openweathermap.org/data/2.5/weather?q=${selectedCity}&lang=it&units=metric&appid=${apiKey}`;
        try {
            const response = await fetch(url);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setWeatherData(data); // Update the weather data state
        } catch (error) {
            setError('Failed to fetch weather data');
        }
    }

    useEffect(() => {
        const form = document.getElementById("city-form");
        form.addEventListener("submit", event => {
            event.preventDefault(); // Prevents form from refreshing page
            const selectedCity = document.getElementById("city-select").value;
            getWeather(selectedCity, apiKey);
        });

        // Clean up event listener when component unmounts
        return () => {
            form.removeEventListener("submit", () => {});
        };
    }, []);

    return (
        <div className="container-fluid animated-gradient py-5">
            <h1 className="title text-center mb-5">IWWA</h1>
            <h2 className="title text-center mb-5">Italian Worst Weather App</h2>
            <div className="row">
                <div className="col-md-6 offset-md-3">
                    <h3 className="title">Display one city</h3>
                    <form id="city-form">
                        <div className="form-group mb-3">
                            <select id="city-select" className="form-select">
                                <option value="Milano">Milano</option>
                                <option value="Roma">Roma</option>
                                <option value="Bologna">Bologna</option>
                                <option value="Palermo">Palermo</option>
                                <option value="Napoli">Napoli</option>
                                <option value="Torino">Torino</option>
                                <option value="Firenze">Firenze</option>
                            </select>
                        </div>
                        <button type="submit" id="display-button" className="btn btn-primary">Display</button>
                    </form>
                </div>
                <div className="col-md-6 offset-md-3">
                    <h3 className="title mt-3">Display Cities using Promise.all</h3>
                    <button id="all-cities-sync" className="btn btn-primary mt-1 mb-3">Display all #1</button>
                    <h3 className="title mt-3">Display Cities one by one</h3>
                    <button id="all-cities" className="btn btn-primary mt-1 mb-3">Display all #2</button>
                </div>
            </div>
            <div className="row mt-5">
                <div id="weather-cards" className="col-md-12 d-flex justify-content-center">
                    {error && <p>{error}</p>}
                    {weatherData && (
                        <div className="card">
                            <h5 className="card-title">{weatherData.name}</h5>
                            <p className="card-text">Temperature: {weatherData.main.temp} °C</p>
                            <p className="card-text">Weather: {weatherData.weather[0].description}</p>
                            <p className="card-text">Humidity: {weatherData.main.humidity} %</p>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Wapp;
