  (function(index) {
    // Set a delay for each star's initial animation
    setTimeout(function() {
      // Call the animate function for the current star
      animate(stars[index]);

      // Set an interval to repeatedly call the animate function for the current star
      setInterval(function() {
        animate(stars[index]);
      }, 1000);
    }, index * (interval / 3));
  })(i);
