import './Wapp.css'
import React from 'react'


function clearWeatherCards() {
  weatherCards.innerHTML = '';
  //removes the weather cards from the div
}

function getWeather(selectedCity, apiKey) {
  clearWeatherCards()
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${selectedCity}&lang=it&units=metric&appid=${apiKey}`;
  //completa la funzione
}

//oppure: 
// async function getWeather(selectedCity, apiKey) {
//   const url = `https://api.openweathermap.org/data/2.5/weather?q=${selectedCity}&lang=it&units=metric&appid=${apiKey}`;
//   riempi se vuoi usare invece async await
//}



function Wapp(){
    const apiKey = "39cca2a3cb09544ba65e52668421a919"; //metti qui la tua chiave
const citySelect = document.getElementById("city-select");
const weatherCards = document.getElementById("weather-cards"); //div to fill with weather cards
const form = document.getElementById("city-form"); //form to get the city
// const cities = ["Milano", "Roma", "Bologna", "Palermo", "Napoli", "Torino", "Firenze"];
//for the home exercise using Promise.all


form.addEventListener("submit", event => {
    event.preventDefault(); // prevents form from refreshing page
    const selectedCity = document.getElementById("city-select").value;
    getWeather(selectedCity, apiKey);
    });
    return(
        <body>
        <div class="container-fluid animated-gradient py-5">
            <h1 class="title text-center mb-5">IWWA</h1>
            <h2 class="title text-center mb-5">Italian Worst Weather App</h2>
            <div class="row">
            <div class="col-md-6 offset-md-3">
                <h3 class="title">Display one city</h3>
                <form id="city-form">
                <div class="form-group mb-3">
                    <select id="city-select" class="form-select">
                    <option value="Milano">Milano</option>
                    <option value="Roma">Roma</option>
                    <option value="Bologna">Bologna</option>
                    <option value="Palermo">Palermo</option>
                    <option value="Napoli">Napoli</option>
                    <option value="Torino">Torino</option>
                    <option value="Firenze">Firenze</option>
                    </select>
                </div>
                <button type="submit" id="display-button" class="btn btn-primary">Display</button>
                </form>
                    </div>
                    <div class="col-md-6 offset-md-3">
                        <h3 class="title mt-3">Display Cities using Promise.all</h3>
                        <button id="all-cities-sync" class="btn btn-primary mt-1 mb-3">Display all #1</button>
                        <h3 class="title mt-3">Display Cities one by one</h3>
                        <button id="all-cities" class="btn btn-primary mt-1 mb-3">Display all #2</button>
                    </div>
                    </div>
                    <div class="row mt-5">
                        <div id="weather-cards" class="col-md-12 d-flex justify-content-center">
                        </div>
                    </div>
                    </div>
            </body>
    );
}
export default Wapp;
