import React from "react";
import './styles.css'

function DiceGame(){
    var rnd= Math.floor((Math.random()*6 +1));
    console.log(rnd);
    var source= "DiceeChallenge/images/dice"+rnd.toString()+".png";

    return(
        <div class='game'>
        <body>
            <div class="container">
            <h1>Refresh Me!</h1>
            <div class="dice">
                <p>Player 1</p>
                <img class="img1" src="images/dice6.png"></img>
            </div>

            <div class="dice">
                <p>Player 2</p>
                <img class="img2" src={source}></img>
            </div>

            </div>    
        </body>    
        </div>
    );
}
export default DiceGame;
